import pytest

from data_store.data_store import DataStore, ValueInvalidException


def test_value_zero():
    with pytest.raises(ValueInvalidException):
        o = DataStore(key='test1')
        o.create(value='')
        o.delete()


def test_value_more_than_16_KB():
    with pytest.raises(ValueInvalidException):
        o = DataStore(key='test2')
        o.create(value= '' )
        o.delete()


def test_value_not_JSON():
    with pytest.raises(TypeError):
        o = DataStore(key='test3')
        o.create(123)
        o.delete()







